import React from 'react';

export default function WelcomeScreen({ onStart }) {
  return (
    <div className="h-screen w-screen bg-black text-white flex flex-col justify-center items-center text-center p-8">
      <h1 className="text-4xl md:text-6xl font-bold mb-4">CardVoyage</h1>
      <p className="text-lg md:text-2xl max-w-xl mb-8">
        A travel game for chaotic friends with questionable taste.  
        Build your mystery travel card. Vote. Reveal. Regret. Repeat.
      </p>
      <button
        onClick={onStart}
        className="bg-white text-black px-6 py-3 rounded font-bold hover:bg-gray-300 transition"
      >
        Start the Game
      </button>
    </div>
  );
}
