import React, { useState } from 'react';
import axios from 'axios';

export default function TravelCardForm() {
  const [formData, setFormData] = useState({
    images: [],
    interests: '',
    budget: '',
    climate: '',
    ecoFriendly: false,
  });

  const [previewImages, setPreviewImages] = useState([]);

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files).slice(0, 5);
    setFormData({ ...formData, images: files });
    const previews = files.map(file => URL.createObjectURL(file));
    setPreviewImages(previews);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.images.length < 1 || formData.images.length > 5) {
      alert('Please upload between 1 and 5 images.');
      return;
    }

    const data = new FormData();
    formData.images.forEach((img) => data.append('images', img));
    data.append('interests', formData.interests);
    data.append('budget', formData.budget);
    data.append('climate', formData.climate);
    data.append('ecoFriendly', formData.ecoFriendly);

    try {
      const response = await axios.post('http://localhost:3001/api/cards', data);
      alert('Travel card created!');
      console.log(response.data);
    } catch (error) {
      console.error('Error submitting form', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md space-y-4">
      <h2 className="text-2xl font-bold text-center">Create Your Travel Card</h2>

      <div>
        <label className="block text-sm font-medium text-gray-700">Upload 1â€“5 Images</label>
        <input
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageUpload}
          className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4
            file:rounded-full file:border-0 file:text-sm file:font-semibold
            file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
        />
        <div className="mt-2 flex gap-2 flex-wrap">
          {previewImages.map((src, idx) => (
            <img
              key={idx}
              src={src}
              alt={`preview-${idx}`}
              className="w-20 h-20 object-cover rounded"
            />
          ))}
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Hashtags/Interests</label>
        <input
          type="text"
          value={formData.interests}
          onChange={(e) => setFormData({ ...formData, interests: e.target.value })}
          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-200"
          placeholder="#beach, #museums, #football"
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Budget ($)</label>
        <input
          type="number"
          value={formData.budget}
          onChange={(e) => setFormData({ ...formData, budget: e.target.value })}
          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-200"
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Preferred Climate</label>
        <select
          value={formData.climate}
          onChange={(e) => setFormData({ ...formData, climate: e.target.value })}
          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-200"
        >
          <option value="">--Select--</option>
          <option value="warm">Warm</option>
          <option value="cold">Cold</option>
          <option value="mild">Mild</option>
        </select>
      </div>

      <div className="flex items-center">
        <input
          type="checkbox"
          checked={formData.ecoFriendly}
          onChange={(e) => setFormData({ ...formData, ecoFriendly: e.target.checked })}
          className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
        />
        <label className="ml-2 block text-sm text-gray-700">Eco-Friendly</label>
      </div>

      <div className="text-center">
        <button
          type="submit"
          className="bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition"
        >
          Submit Travel Card
        </button>
      </div>
    </form>
  );
}
